mapper.select:
  		SELECT * FROM 
			(
				SELECT ROWNUM R, B.* FROM 
					(
						SELECT * 
						FROM BOARD

						ORDER BY BOARDNUM DESC
					) B
			)
		WHERE R BETWEEN #{startRow} AND #{lastRow}

mapper.update:
		UPDATE BOARD SET 
  		BOARDWRITER=#{boardWriter}, 
  		BOARDNAME=#{boardName}, 
  		BOARDCONTENTS=#{boardContents}
  		WHERE BOARDNUM=#{boardNum}
